mapper.update:
UPDATE
    adgomin_user
SET phone_number_certification = #{phoneNumberCertification}
WHERE 1=1
AND user_order = #{userOrder}

mapper.select:
SELECT
    COUNT(*) AS count
FROM adgomin_user
WHERE 1=1
AND user_order = #{userOrder}
AND phone_number_certification = #{phoneNumberCertification}

mapper.update:
UPDATE
    adgomin_user
SET name = #{name}
,phone_number = #{phoneNumber}
,phone_number_yn = 1
WHERE 1=1
AND user_order = #{userOrder}
