mapper.update:
UPDATE
    adgomin_user
SET phone_number_certification = #{phoneNumberCertification}
WHERE 1=1
AND user_order = #{userOrder}

mapper.select:
SELECT
    COUNT(*) AS count
FROM adgomin_user
WHERE 1=1
AND user_order = #{userOrder}
AND phone_number_certification = #{phoneNumberCertification}

mapper.update:
UPDATE
    adgomin_user
SET name = #{name}
,phone_number = #{phoneNumber}
,phone_number_yn = 1
WHERE 1=1
AND user_order = #{userOrder}

UPDATE
    adgomin_payments
SET payment_status = 'FAILURE'
,payment_key = #{paymentKey}
,payment_type = #{paymentType}
,payment_failure_reason = #{paymentFailureReason}
,payment_amount = #{paymentAmount}
,modify_dt = NOW()
WHERE 1=1
AND order_id = #{orderId}

UPDATE
    adgomin_payments
SET payment_status = 'SUCCESS'
  ,payment_key = #{paymentKey}
  ,payment_type = #{paymentType}
  ,payment_amount = #{paymentAmount}
  ,modify_dt = NOW()
WHERE 1=1
AND order_id = #{orderId}
