mapper.update:
UPDATE
    adin_media_register
SET media_detail_explain = #{mediaDetailExplain}
  ,media_submit_status = #{mediaSubmitStatus}
WHERE 1=1
  AND media_order = #{mediaOrder}

mapper.select:
SELECT
    COUNT(*) AS count
FROM adin_user
WHERE 1=1
AND user_type = "media"

    AND marketing_yn = #{marketingYn}


    AND certified_yn = #{certifiedYn}


    AND use_yn = #{useYn}

SELECT email AS email ,nickname AS nickname ,marketing_yn AS marketingYn ,use_yn AS useYn ,certified_yn AS certifiedYn ,create_dt AS createDt FROM adin_user WHERE 1=1 AND user_type = "media"
AND marketing_yn = #{marketingYn}
AND certified_yn = #{certifiedYn}
AND use_yn = #{useYn}
ORDER BY create_dt LIMIT #{pageStart}, #{perPageNum}

SELECT
    COUNT(*) AS count
FROM adin_user
WHERE 1=1
AND user_type = "advertiser"

    AND marketing_yn = #{marketingYn}


    AND certified_yn = #{certifiedYn}


    AND use_yn = #{useYn}

SELECT email AS email ,nickname AS nickname ,marketing_yn AS marketingYn ,use_yn AS useYn ,certified_yn AS certifiedYn ,create_dt AS createDt FROM adin_user WHERE 1=1 AND user_type = "advertiser"
AND marketing_yn = #{marketingYn}
AND certified_yn = #{certifiedYn}
AND use_yn = #{useYn}
ORDER BY create_dt LIMIT #{pageStart}, #{perPageNum}
