mapper.update:
UPDATE
    adin_media_introduce
SET media_introduce = #{mediaIntroduce}
,region = #{region}
,ad_category = #{adCategory}
,media_url = #{mediaUrl}
WHERE 1=1
  AND email = #{email}

UPDATE
    adin_media_register
SET ad_detail_category = #{adDetailCategory}
  ,media_title = #{mediaTitle}
  ,media_summary = #{mediaSummary}
  ,media_detail_explain = #{mediaDetailExplain}
  ,media_price = #{mediaPrice}
WHERE 1=1
  AND email = #{email}
  AND media_order = (
    SELECT
        max_media_order
    FROM
    (SELECT
        MAX(media_order) AS max_media_order
    FROM adin_media_register
    WHERE email = #{email}) AS sub
  )
