mapper.update:
UPDATE
    adin_portfolio
SET main_img_nm = #{mainImgNm}
  ,main_origin_file_nm = #{mainOriginFileNm}
  ,main_img_file_path = #{mainImgFilePath}
WHERE 1=1
  AND email = #{email}
  AND portfolio_order = (
    SELECT
        max_portfolio_order
    FROM
        (SELECT
             MAX(portfolio_order) AS max_portfolio_order
         FROM adin_portfolio
         WHERE email = #{email}) AS sub
)
