mapper.update:
UPDATE
    adin_user
SET profile_img_nm = #{profileImgNm}
,profile_origin_file_nm = #{profileOriginFileNm}
,profile_img_file_path = #{profileImgFilePath}
WHERE 1=1
  AND email = #{email}

UPDATE
    adin_user
SET nickname = #{nickname}
WHERE 1=1
  AND email = #{email}

UPDATE
    adin_user
SET password = #{password}
WHERE 1=1
  AND email = #{email}
